import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/");
    }
  }, [navigate]);

  const logout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <div className="bg-light min-vh-100">
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-primary shadow">
        <div className="container">
          <span className="navbar-brand fw-bold">üéâ Event Dashboard</span>
          <button className="btn btn-danger" onClick={logout}>
            Logout
          </button>
        </div>
      </nav>

      <div className="container mt-4">
        {/* Welcome Card */}
        <div className="card shadow-sm mb-4">
          <div className="card-body">
            <h4 className="card-title">Welcome back üëã</h4>
            <p className="card-text text-muted">
              Manage your events and registrations here.
            </p>
          </div>
        </div>

        {/* Stats Row */}
        <div className="row mb-4">
          <div className="col-md-4 mb-3">
            <div className="card text-center shadow-sm border-0">
              <div className="card-body">
                <h5 className="text-primary">üéü Total Events</h5>
                <h2>12</h2>
              </div>
            </div>
          </div>

          <div className="col-md-4 mb-3">
            <div className="card text-center shadow-sm border-0">
              <div className="card-body">
                <h5 className="text-success">üìÖ Upcoming</h5>
                <h2>5</h2>
              </div>
            </div>
          </div>

          <div className="col-md-4 mb-3">
            <div className="card text-center shadow-sm border-0">
              <div className="card-body">
                <h5 className="text-danger">‚è≥ Past Events</h5>
                <h2>7</h2>
              </div>
            </div>
          </div>
        </div>

        {/* Events Grid */}
        <h5 className="mb-3">My Registered Events</h5>

        <div className="row">
          {[1, 2, 3].map((event) => (
            <div key={event} className="col-md-4 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title">Tech Conference {event}</h5>
                  <p className="card-text text-muted">üìç Chennai</p>
                  <p className="card-text text-muted">üìÖ 2026-03-15</p>
                  <button className="btn btn-outline-primary w-100">
                    View Details
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
